import type { RTEmbedNode, RTImageNode, RTLinkNode } from "@prismicio/client";
import type { Element } from "hast";
import type { RTPartialInlineNode } from "./RichTextFieldBuilder";
/**
 * Serializes a hast {@link Element} node to a {@link RTImageNode}.
 *
 * @param node - A hast node to serialize.
 *
 * @returns Equivalent {@link RTImageNode}.
 */
export declare const serializeImage: (node: Element) => RTImageNode;
/**
 * Serializes a hast {@link Element} node to a {@link RTEmbedNode}.
 *
 * @param node - A hast node to serialize.
 *
 * @returns Equivalent {@link RTEmbedNode}.
 */
export declare const serializeEmbed: (node: Element) => RTEmbedNode;
/**
 * Serializes a hast {@link Element} node to a {@link RTPartialInlineNode}.
 *
 * @param node - A hast node to serialize.
 * @param rtPartialInlineNode - A partial rich text node.
 *
 * @returns Equivalent {@link RTPartialInlineNode}.
 */
export declare const serializeSpan: (node: Element, rtPartialInlineNode: RTPartialInlineNode | Omit<RTLinkNode, "start" | "end" | "data">) => RTPartialInlineNode;
